import React, { Component } from "react";
import { Grid, Col, Row, Image, Carousel } from "react-bootstrap";
import "./Carousel.css";

class ControlledCarousel extends Component {
  constructor(props, context) {
    super(props, context);

    this.handleSelect = this.handleSelect.bind(this);

    this.state = {
      index: 0,
      direction: null
    };
  }
  handleSelect(selectedIndex, e) {
    this.setState({
      index: selectedIndex,
      direction: e.direction
    });
  }

  render() {
    const { index, direction } = this.state;

    return (
      <Carousel
        activeIndex={index}
        direction={direction}
        onSelect={this.handleSelect}
      >
        <Carousel.Item className="carousel-outer blur-20px">
          <Row>
            <Col xs={4} sm={4} md={4}>
              <img
                className="carousel-image"
                src="https://m.media-amazon.com/images/M/MV5BMjI1MDQ2MDg5Ml5BMl5BanBnXkFtZTgwMjc2NjM5ODE@._V1_SY1000_SX675_AL_.jpg"
              />
            </Col>
            <Col xs={8} sm={8} md={8}>
              <h3 className="movie-title">Hunt for the Wilderpeople</h3>
              <p className="movie-description">
                Raised on hip-hop and foster care, defiant city kid Ricky gets a
                fresh start in the New Zealand countryside. From the director of
                What We Do In The Shadown.
              </p>
            </Col>
          </Row>
        </Carousel.Item>
        <Carousel.Item>
          <img
            width={900}
            height={500}
            alt="900x500"
            src="https://m.media-amazon.com/images/M/MV5BMjI1MDQ2MDg5Ml5BMl5BanBnXkFtZTgwMjc2NjM5ODE@._V1_SY1000_SX675_AL_.jpg"
          />
          <Carousel.Caption>
            <h3>Second slide label</h3>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img width={900} height={500} alt="900x500" src="/carousel.png" />
          <Carousel.Caption />
        </Carousel.Item>
      </Carousel>
    );
  }
}

//render(<ControlledCarousel />);

export default ControlledCarousel;
