import React, { Component } from "react";
import Images from "./Images.jsx";
import LeftArrowButton from "./LeftArrowButton.jsx";
import RightArrowButton from "./RightArrowButton.jsx";
/*import { Link } from "react-router-dom";
import { Grid, Col, Row, Image } from "react-bootstrap";*/
import "./Carousel.css";

class Carousel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentImage: 0,
      translateValue: 0,
      images: [
        "https://m.media-amazon.com/images/M/MV5BOGVhNjUwYTItYmI2NS00ZGI1LWE5ZDQtMWVjMTIyODAzMDI4XkEyXkFqcGdeQXVyMjQ3NzUxOTM@._V1_.jpg"
      ]
    };
  }

  onLeftClick = () => {
    if (this.state.currentIndex === 0) return;

    this.setState(prevState => ({
      currentIndex: prevState.currentIndex - 1,
      translateValue: prevState.translateValue + this.slideWidth()
    }));
  };

  onRightClick = () => {
    if (this.state.currentIndex === this.state.images.length - 1) {
      return this.setState({
        currentIndex: 0,
        translateValue: 0
      });
    }

    // This will not run if we met the if condition above
    this.setState(prevState => ({
      currentIndex: prevState.currentIndex + 1,
      translateValue: prevState.translateValue + -this.slideWidth()
    }));
  };

  slideWidth = () => {
    return document.querySelector(".image").clientWidth;
  };

  render() {
    return (
      <div className="carousel">
        <div
          className="images-wrapper"
          style={{
            transform: `translateX(${this.state.translateValue}px)`,
            transition: "transform ease-out 0.45s"
          }}
        >
          {this.state.images.map((image, i) => (
            <Images key={i} image={image} />
          ))}
        </div>
        <LeftArrowButton onLeftClick={this.onLeftClick} />

        <RightArrowButton onRightClick={this.onRightClick} />
      </div>
    );
  }
}

export default Carousel;
