import React, { Component } from "react";
import Images from "./Images.jsx";
import LeftArrowButton from "./LeftArrowButton.jsx";
import RightArrowButton from "./RightArrowButton.jsx";
/*import { Link } from "react-router-dom";
import { Grid, Col, Row, Image } from "react-bootstrap";*/
import "./Carousel.css";

class Carousel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentImage: 0,
      images: [
        "https://m.media-amazon.com/images/M/MV5BOGVhNjUwYTItYmI2NS00ZGI1LWE5ZDQtMWVjMTIyODAzMDI4XkEyXkFqcGdeQXVyMjQ3NzUxOTM@._V1_.jpg"
      ]
    };
  }

  onLeftClick = () => {
    this.setState(prevState => ({
      currentImage: prevState.currentImage - 1
    }));
  };

  onRightClick = () => {
    this.setState(prevState => ({
      currentImage: prevState.currentImage + 1
    }));
  };

  render() {
    return (
      <div className="carousel">
        {" "}
        {this.state.images.map((image, currentImage) => (
          <Images key={currentImage} image={image} />
        ))}
      </div>
    );
  }
}

export default Carousel;
